import React, { useEffect, useState } from 'react';
import './Dashboard.css';
import 'jspdf-autotable';

const Dashboard = () => {
  const [userData, setUserData] = useState([]);
  const [csvData, setCsvData] = useState("");
  const [currentIndex, setCurrentIndex] = useState(0);
  const [jobData, setJobData] = useState([]);

  useEffect(() => {
    fetchData();
    fetchDataa();

    const intervalId = setInterval(() => {
      setCurrentIndex((prevIndex) => prevIndex + 1);
    }, 5 * 1000); // 10-second interval

    return () => clearInterval(intervalId);
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/users');
      const jsonData = await response.json();
      setUserData(jsonData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const fetchDataa = async () => {
    try {
      const response = await fetch("http://192.168.1.108:5000");
      const data = await response.json();
      setJobData(data.job_data);
      const csvData = JSON.stringify(data, null, 2);
      setCsvData(csvData);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const downloadCSV = () => {
    const formattedCSVData = formatCSV(csvData);
    const href = `data:text/csv;charset=utf-8,${encodeURIComponent(formattedCSVData)}`;
    const a = document.createElement('a');
    a.href = href;
    a.download = 'filename.csv';
    a.click();
  };

  const formatCSV = (csvData) => {
    const lines = csvData.split('\n');
    const formattedLines = [];
    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];
      let formattedLine = line.split(',');
      formattedLine = formattedLine.map((cell) => {
        return cell.trim();
      });
      formattedLines.push(formattedLine.join(','));
    }
    return formattedLines.join('\n');
  };

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      {userData.length > 0 ? (
  <table>
  <thead>
    <tr>
      <th>Category</th>
      <th>Company</th>
      <th>Company URL</th>
      <th>Job Title</th>
      <th>Location</th>
      <th>Skills</th>
    </tr>
  </thead>
  <tbody>
    {jobData.map((job, index) => (
      <tr key={index}>
        <td>{job.Category}</td>
        <td>{job.Company}</td>
        <td>{job['Company URL']}</td>
        <td>{job['Job Title']}</td>
        <td>{job.Location}</td>
        <td>{job.Skills}</td>
        <td>
<button
  onClick={() => {
    downloadCSV();
  }}
  className="btn"
>
  download
</button>
</td>

      </tr>
    ))}
  </tbody>
</table>
      ) : (
        <p>Loading...</p>
      )}

      {jobData.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Category</th>
              <th>Company</th>
              <th>Company URL</th>
              <th>Job Title</th>
              <th>Location</th>
              <th>Skills</th>
            </tr>
          </thead>
          <tbody>
            {jobData.map((job, index) => (
              <tr key={index}>
                <td>{job.Category}</td>
                <td>{job.Company}</td>
                <td>{job['Company URL']}</td>
                <td>{job['Job Title']}</td>
                <td>{job.Location}</td>
                <td>{job.Skills}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Loading job data...</p>
      )}
    </div>
  );
};

export default Dashboard;



